const axios = require('axios');

class ApiClient {
  constructor(baseURL) {
    this.http = axios.create({ baseURL });
  }

  async getUser(userId, options = {}) {
    const res = await this.http.get(`/users/${userId}`, { params: options });
    return res.data;
  }
}

module.exports = { ApiClient };
